import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import '../movieDetails.css'; // Import the CSS file

function MovieDetails() {
  const { id } = useParams();
  const [movie, setMovie] = useState(null);

  useEffect(() => {
    async function fetchMovieDetails() {
      try {
        const response = await axios.get(
          `https://api.themoviedb.org/3/movie/${id}`,
          {
            params: {
              api_key: '9724ee7735f6229f16425966d22d4ff6',
            },
          }
        );
        setMovie(response.data);
      } catch (error) {
        console.error('Error fetching movie details:', error);
      }
    }

    fetchMovieDetails();
  }, [id]);

  return (
    <div className="movie-details-container">
      {movie ? (
        <div>
          <div
            className="hero-section"
            style={{
              backgroundImage: `url(https://image.tmdb.org/t/p/original/${movie.poster_path})`,
              backgroundSize: 'cover', // This ensures the image covers the entire div
              backgroundPosition: 'center', // Center the image both horizontally and vertically
            }}
          >

          </div>

          <h1 className="movie-title" data-testid="movie-title">{movie.title}</h1>
          <p className="movie-release-date" data-testid="movie-release-date">Release Date (UTC): {movie.release_date}</p>
          <p className="movie-runtime" data-testid="movie-runtime">Runtime: {movie.runtime} minutes</p>
          <p className="movie-overview" data-testid="movie-overview">{movie.overview}</p>


        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
}

export default MovieDetails;
