import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import '../movieDetails.css'; // Import the CSS file
import { Link } from 'react-router-dom';

function MovieDetails() {
  const { id } = useParams();
  const [movie, setMovie] = useState(null);

  useEffect(() => {
    async function fetchMovieDetails() {
      try {
        const response = await axios.get(
          `https://api.themoviedb.org/3/movie/${id}`,
          {
            params: {
              api_key: '9724ee7735f6229f16425966d22d4ff6',
            },
          }
        );
        setMovie(response.data);
      } catch (error) {
        console.error('Error fetching movie details:', error);
      }
    }

    fetchMovieDetails();
  }, [id]);

  return (
    <div className="movie-details-container">
      <div className="nabar">
        <Link to="/">Home</Link>
        <Link to="/movies">Movies</Link>
        <Link to="/tv-series">TV Series</Link>
        <Link to="/upcoming">Upcoming</Link>
      </div>

      {movie ? (
        <div className='content'>
          <div
            className="her-section"
            style={{
              backgroundImage: `url(https://image.tmdb.org/t/p/original/${movie.poster_path})`,
              backgroundSize: 'cove', // This ensures the image covers the entire div
              backgroundPosition: 'cente', // Center the image both horizontally and vertically
            }}
          >
            {/* Watch trailer button */}
            <a
              href={`https://www.youtube.com/watch?v=${movie.trailer_key}`}
              target="_blank"
              rel="noopener noreferrer"
              className="watch-trailer-button"

            >
              <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path d="M3 3V21L11 15L21 21V3L11 9L3 3Z" fill="#FF3E3E"/>
              </svg>
              Watch Trailer
            </a>
          </div>

          <h1 className="movie-title" data-testid="movie-title">{movie.title}</h1>
          <p className="movie-release-date" data-testid="movie-release-date">Release Date (UTC): {movie.release_date}</p>
          <p className="movie-runtime" data-testid="movie-runtime">Runtime: {movie.runtime} minutes</p>
          <p className="movie-overview" data-testid="movie-overview">{movie.overview}</p>

        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
}

export default MovieDetails;
