import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Card from './Card'; // Import the Card component
import './HomePage.css'; // Import your CSS file for styling

function HomePage() {
  const [topRatedMovies, setTopRatedMovies] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [randomMovie, setRandomMovie] = useState(null);

  useEffect(() => {
    async function fetchTopRatedMovies() {
      try {
        const response = await axios.get(
          'https://api.themoviedb.org/3/movie/top_rated?language=en-US&page=1',
          {
            params: {
              api_key: '9724ee7735f6229f16425966d22d4ff6',
            },
          }
        );
        setTopRatedMovies(response.data.results.slice(0, 10));
      } catch (error) {
        console.error('Error fetching top-rated movies:', error);
      }
    }

    fetchTopRatedMovies();
  }, []);

  useEffect(() => {
    async function fetchRandomMovie() {
      try {
        const response = await axios.get(
          'https://api.themoviedb.org/3/movie/popular',
          {
            params: {
              api_key: '9724ee7735f6229f16425966d22d4ff6',
            },
          }
        );

        const randomIndex = Math.floor(Math.random() * response.data.results.length);
        const randomMovieData = response.data.results[randomIndex];
        setRandomMovie(randomMovieData);
      } catch (error) {
        console.error('Error fetching random movie:', error);
      }
    }

    fetchRandomMovie();
  }, []);

  const handleSearch = async () => {
    if (searchQuery.trim() === '') {
      // Don't perform an empty search
      return;
    }

    setLoading(true);

    try {
      const response = await axios.get(
        'https://api.themoviedb.org/3/search/movie',
        {
          params: {
            api_key: '9724ee7735f6229f16425966d22d4ff6',
            query: searchQuery,
          },
        }
      );
      setSearchResults(response.data.results);
    } catch (error) {
      console.error('Error fetching search results:', error);
    }

    setLoading(false);
  };

  return (
    <div className="homepage-container">
      <div className="hero-section" style={{ backgroundImage: `url(https://image.tmdb.org/t/p/original${randomMovie?.backdrop_path})` }}>
        <div className="hero-content">
          <h1>Discover a New Movie</h1>
          <p>Explore and find your favorite movies</p>
          <input
            type="text"
            placeholder="Search for a movie..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
          <button onClick={handleSearch}>Search</button>
        </div>
      </div>

      <div className="search-results">
        {loading && <p>Loading...</p>}
        {searchResults.map((movie) => (
          <Card key={movie.id} movie={movie} />
        ))}
      </div>

      <h1>Top 10 Movies</h1>
      <div className="movie-grid">
        {topRatedMovies.map((movie) => (
          <Card key={movie.id} movie={movie} />
        ))}
      </div>
    </div>
  );
}

export default HomePage;
